preorder(node)
    if (node = null)
        return
    visit(node)
    for (i = 0, i < node.child.length, i++)
        preorder(node.child[i])

reverse_preorder(node)
    if (node = null)
        return
    for (i = node.child.length - 1, i >= 0, i-- )
    reverse_preorder(node.child[i])
        visit(node)

interior_indirect(data, segment, level, k, m, children) # Reserve space at the head of the segment for this node's direct pointers before # descending to children. We want the top of the tree packed. reserve_count = min(m, segment.tail - segment.head) segment.head += reserve_count
while len(children) < m and not segment.head == segment.tail: child = bottom_up_preorder(data, segment, level - 1, k, m) # prepend children.insert(0, child)
# Pull back our reservation and put those pointers in our direct children segment.head -= reserve_count

